mapper.select:
select rnum, bname, pname, img1, pprice, pcolorId, d1name, d2name, d3name, color_img, pcommonid from ( select rownum as rnum, bname, pname, img1, pprice, pcolorId, d1name, d2name, d3name, color_img, pcommonid from ( select brand.bname as bname, pcommon.pname as pname, pcolor.img1 as img1, pcolor.pprice as pprice, pcolor.pcolorid as pcolorId, pcolor.color_img as color_img, pcolor.pcommonid as pcommonid, category.d1name as d1name, category.d2name as d2name, category.d3name as d3name from product_color pcolor join product_common pcommon on (pcolor.pcommonid = pcommon.pcommonid) join brand brand on(pcommon.bno = brand.bno) join product_category pcategory on(pcolor.pcommonid = pcategory.pcommonid) join category category on(category.catno = pcategory.catno) where category.catno in (select catno from category where d1name=#{depth1}
and d2name=#{depth2}
and d3name=#{depth3}
) order by pcolorId ) where rownum <=#{pager.endRowNo} ) where rnum >=#{pager.startRowNo}

select count(*) from( select brand.bname as bname, pcommon.pname as pname, pcolor.img1 as img1, pcolor.pprice as pprice, pcolor.pcolorid as pcolorId, pcolor.color_img as color_img, pcolor.pcommonid as pcommonid, category.d1name as d1name, category.d2name as d2name, category.d3name as d3name from product_color pcolor join product_common pcommon on (pcolor.pcommonid = pcommon.pcommonid) join brand brand on(pcommon.bno = brand.bno) join product_category pcategory on(pcolor.pcommonid = pcategory.pcommonid) join category category on(category.catno = pcategory.catno) where category.catno in (select catno from category where d1name=#{depth1}
and d2name=#{depth2}
and d3name=#{depth3}
) order by pcolorId )
